stateTransitions:
{
  "phases": [
    "init",
    "weapon_creation",
    "opponent_reveal",
    "round_selection",
    "round_resolution",
    "finished"
  ],
  "phaseMetadata": [
    {
      "phase": "init",
      "requiresPlayerInput": false
    },
    {
      "phase": "weapon_creation",
      "requiresPlayerInput": true
    },
    {
      "phase": "opponent_reveal",
      "requiresPlayerInput": false
    },
    {
      "phase": "round_selection",
      "requiresPlayerInput": true
    },
    {
      "phase": "round_resolution",
      "requiresPlayerInput": false
    },
    {
      "phase": "finished",
      "requiresPlayerInput": false
    }
  ],
  "transitions": [
    {
      "id": "initialize_game",
      "fromPhase": "init",
      "toPhase": "weapon_creation",
      "condition": "Game starts - display game show opening and initialize state for weapon creation",
      "checkedFields": [
        "game.currentPhase"
      ],
      "preconditions": [
        {
          "id": "game_not_initialized",
          "logic": {
            "==": [
              {
                "var": "game.currentPhase"
              },
              "init"
            ]
          },
          "deterministic": true,
          "explain": "Check if game.currentPhase == 'init'"
        }
      ],
      "humanSummary": "Display boisterous game show opening, initialize game state (currentRound=0, gameEnded=false), set all players to actionRequired=true for weapon creation, and transition to weapon_creation phase"
    },
    {
      "id": "weapons_created",
      "fromPhase": "weapon_creation",
      "toPhase": "opponent_reveal",
      "condition": "Both players have completed weapon creation (3 weapons each)",
      "checkedFields": [
        "game.currentPhase",
        "players[*].weapons",
        "players[*].actionRequired"
      ],
      "preconditions": [
        {
          "id": "in_weapon_creation",
          "logic": {
            "==": [
              {
                "var": "game.currentPhase"
              },
              "weapon_creation"
            ]
          },
          "deterministic": true,
          "explain": "Check if game.currentPhase == 'weapon_creation'"
        },
        {
          "id": "all_players_have_weapons",
          "logic": {
            "allPlayers": [
              "weapons",
              "!=",
              null
            ]
          },
          "deterministic": true,
          "explain": "All players have created their weapons array (players[*].weapons != null)"
        },
        {
          "id": "all_creation_complete",
          "logic": {
            "allPlayers": [
              "actionRequired",
              "==",
              false
            ]
          },
          "deterministic": true,
          "explain": "All players have completed weapon creation (players[*].actionRequired == false)"
        }
      ],
      "humanSummary": "After both players create 3 weapons, assign RPS mappings to each weapon and transition to opponent_reveal phase"
    },
    {
      "id": "reveal_complete",
      "fromPhase": "opponent_reveal",
      "toPhase": "round_selection",
      "condition": "Opponent weapons have been revealed to both players",
      "checkedFields": [
        "game.currentPhase",
        "game.currentRound"
      ],
      "preconditions": [
        {
          "id": "in_opponent_reveal",
          "logic": {
            "==": [
              {
                "var": "game.currentPhase"
              },
              "opponent_reveal"
            ]
          },
          "deterministic": true,
          "explain": "Check if game.currentPhase == 'opponent_reveal'"
        },
        {
          "id": "reveal_processed",
          "logic": {
            "==": [
              {
                "var": "game.currentRound"
              },
              0
            ]
          },
          "deterministic": true,
          "explain": "Check that game.currentRound == 0 (reveal happens before any rounds)"
        }
      ],
      "humanSummary": "Display bombastic narrative revealing opponent weapons to both players, set currentRound=1, mark players as actionRequired=true, and transition to round_selection"
    },
    {
      "id": "weapons_selected",
      "fromPhase": "round_selection",
      "toPhase": "round_resolution",
      "condition": "Both players have selected weapons for the current round",
      "checkedFields": [
        "game.currentPhase",
        "players[*].selectedWeapon",
        "players[*].actionRequired"
      ],
      "preconditions": [
        {
          "id": "in_round_selection",
          "logic": {
            "==": [
              {
                "var": "game.currentPhase"
              },
              "round_selection"
            ]
          },
          "deterministic": true,
          "explain": "Check if game.currentPhase == 'round_selection'"
        },
        {
          "id": "both_selected",
          "logic": {
            "allPlayers": [
              "selectedWeapon",
              "!=",
              null
            ]
          },
          "deterministic": true,
          "explain": "All players have selected a weapon (players[*].selectedWeapon != null)"
        },
        {
          "id": "all_selections_complete",
          "logic": {
            "allPlayers": [
              "actionRequired",
              "==",
              false
            ]
          },
          "deterministic": true,
          "explain": "All players have completed weapon selection (players[*].actionRequired == false)"
        }
      ],
      "humanSummary": "After both players select weapons, transition to round_resolution to determine outcome"
    },
    {
      "id": "match_continues",
      "fromPhase": "round_resolution",
      "toPhase": "round_selection",
      "condition": "Round resolved but no player has won 2 rounds yet",
      "checkedFields": [
        "game.currentPhase",
        "game.currentRound",
        "players[*].roundsWon"
      ],
      "preconditions": [
        {
          "id": "in_round_resolution",
          "logic": {
            "==": [
              {
                "var": "game.currentPhase"
              },
              "round_resolution"
            ]
          },
          "deterministic": true,
          "explain": "Check if game.currentPhase == 'round_resolution'"
        },
        {
          "id": "no_player_has_two_wins",
          "logic": {
            "!": {
              "anyPlayer": [
                "roundsWon",
                ">=",
                2
              ]
            }
          },
          "deterministic": true,
          "explain": "No player has won 2 rounds yet (no player has roundsWon >= 2)"
        },
        {
          "id": "rounds_remaining",
          "logic": {
            "<": [
              {
                "var": "game.currentRound"
              },
              3
            ]
          },
          "deterministic": true,
          "explain": "Check that game.currentRound < 3 (match can continue)"
        }
      ],
      "humanSummary": "Resolve round outcome using RPS logic and optional wrestling-style reversal, generate 2-4 sentence boisterous narrative, update roundsWon, clear selectedWeapon, increment currentRound, set actionRequired=true, and continue to next round"
    },
    {
      "id": "match_won",
      "fromPhase": "round_resolution",
      "toPhase": "finished",
      "condition": "A player has won 2 rounds and the match",
      "checkedFields": [
        "game.currentPhase",
        "players[*].roundsWon"
      ],
      "preconditions": [
        {
          "id": "in_round_resolution",
          "logic": {
            "==": [
              {
                "var": "game.currentPhase"
              },
              "round_resolution"
            ]
          },
          "deterministic": true,
          "explain": "Check if game.currentPhase == 'round_resolution'"
        },
        {
          "id": "player_has_two_wins",
          "logic": {
            "anyPlayer": [
              "roundsWon",
              ">=",
              2
            ]
          },
          "deterministic": true,
          "explain": "At least one player has won 2 rounds (any player has roundsWon >= 2)"
        }
      ],
      "humanSummary": "Resolve final round, generate celebratory narrative with winner announcement and ridiculous prize, set gameEnded=true, clear actionRequired, and end match"
    }
  ]
}

playerPhaseInstructions.weapon_creation:
{
  "phase": "weapon_creation",
  "playerActions": [
    {
      "id": "create-weapon",
      "actionName": "Create Weapon",
      "description": "Player submits one weapon description (any length, any style)",
      "validation": {
        "checks": [
          {
            "id": "wrongPhase",
            "logic": {
              "==": [
                {
                  "var": "game.currentPhase"
                },
                "weapon_creation"
              ]
            },
            "errorMessage": "Cannot create weapon - not in weapon creation phase"
          },
          {
            "id": "tooManyWeapons",
            "logic": {
              "<": [
                {
                  "var": "players.{{playerId}}.weapons.length"
                },
                3
              ]
            },
            "errorMessage": "You already have 3 weapons - cannot create more"
          },
          {
            "id": "missingDescription",
            "logic": {
              "!!": {
                "var": "input.weaponDescription"
              }
            },
            "errorMessage": "Weapon description is required"
          }
        ]
      },
      "mechanicsGuidance": null,
      "stateDelta": [
        {
          "op": "append",
          "path": "players.{{playerId}}.weapons",
          "value": "{{input.weaponDescription}}"
        },
        {
          "op": "set",
          "path": "players.{{playerId}}.actionRequired",
          "value": "{{weaponsRemaining}}"
        }
      ],
      "messages": {
        "private": [
          {
            "to": "{{playerId}}",
            "template": "{{confirmationMessage}}"
          }
        ],
        "public": null
      },
      "requiredStateFields": [
        "game.currentPhase",
        "players.{{playerId}}.weapons"
      ]
    },
    {
      "id": "request-weapon-suggestions",
      "actionName": "Request Weapon Suggestions",
      "description": "Player requests 3 random weapon suggestions from the game",
      "validation": {
        "checks": [
          {
            "id": "wrongPhase",
            "logic": {
              "==": [
                {
                  "var": "game.currentPhase"
                },
                "weapon_creation"
              ]
            },
            "errorMessage": "Cannot request suggestions - not in weapon creation phase"
          }
        ]
      },
      "mechanicsGuidance": null,
      "stateDelta": [],
      "messages": {
        "private": [
          {
            "to": "{{playerId}}",
            "template": "{{suggestions}}"
          }
        ],
        "public": null
      },
      "requiredStateFields": [
        "game.currentPhase"
      ]
    },
    {
      "id": "accept-weapon-suggestion",
      "actionName": "Accept Weapon Suggestion",
      "description": "Player confirms one of the suggested weapons to use",
      "validation": {
        "checks": [
          {
            "id": "wrongPhase",
            "logic": {
              "==": [
                {
                  "var": "game.currentPhase"
                },
                "weapon_creation"
              ]
            },
            "errorMessage": "Cannot accept suggestion - not in weapon creation phase"
          },
          {
            "id": "tooManyWeapons",
            "logic": {
              "<": [
                {
                  "var": "players.{{playerId}}.weapons.length"
                },
                3
              ]
            },
            "errorMessage": "You already have 3 weapons - cannot add more"
          },
          {
            "id": "missingDescription",
            "logic": {
              "!!": {
                "var": "input.weaponDescription"
              }
            },
            "errorMessage": "Weapon description is required"
          }
        ]
      },
      "mechanicsGuidance": null,
      "stateDelta": [
        {
          "op": "append",
          "path": "players.{{playerId}}.weapons",
          "value": "{{input.weaponDescription}}"
        },
        {
          "op": "set",
          "path": "players.{{playerId}}.actionRequired",
          "value": "{{weaponsRemaining}}"
        }
      ],
      "messages": {
        "private": [
          {
            "to": "{{playerId}}",
            "template": "{{confirmationMessage}}"
          }
        ],
        "public": null
      },
      "requiredStateFields": [
        "game.currentPhase",
        "players.{{playerId}}.weapons"
      ]
