playerPhaseInstructions.round_selection:
{
  "phase": "round_selection",
  "playerActions": [
    {
      "id": "select-weapon",
      "actionName": "Select Weapon",
      "description": "Player selects one of their 3 weapons to deploy this round",
      "validation": {
        "checks": [
          {
            "id": "wrongPhase",
            "logic": {
              "==": [
                {
                  "var": "game.currentPhase"
                },
                "round_selection"
              ]
            },
            "errorMessage": "Cannot select weapon - not in round selection phase"
          },
          {
            "id": "notEnoughWeapons",
            "logic": {
              "==": [
                {
                  "var": "players.{{playerId}}.weapons.length"
                },
                3
              ]
            },
            "errorMessage": "You must have 3 weapons to select from"
          },
          {
            "id": "invalidIndex",
            "logic": {
              "and": [
                {
                  ">=": [
                    {
                      "var": "input.weaponIndex"
                    },
                    0
                  ]
                },
                {
                  "<=": [
                    {
                      "var": "input.weaponIndex"
                    },
                    2
                  ]
                }
              ]
            },
            "errorMessage": "Weapon index must be 0, 1, or 2"
          },
          {
            "id": "alreadySelected",
            "logic": {
              "==": [
                {
                  "var": "players.{{playerId}}.selectedWeapon"
                },
                null
              ]
            },
            "errorMessage": "You have already selected a weapon for this round"
          }
        ]
      },
      "mechanicsGuidance": null,
      "stateDelta": [
        {
          "op": "set",
          "path": "players.{{playerId}}.selectedWeapon",
          "value": "{{input.weaponIndex}}"
        },
        {
          "op": "set",
          "path": "players.{{playerId}}.actionRequired",
          "value": false
        }
      ],
      "messages": {
        "private": [
          {
            "to": "{{playerId}}",
            "template": "Weapon selected: {{weaponName}}. Waiting for opponent..."
          }
        ],
        "public": null
      },
      "requiredStateFields": [
        "game.currentPhase",
        "players.{{playerId}}.weapons",
        "players.{{playerId}}.selectedWeapon",
        "game.currentRound"
      ]
    }
  ]
}

