transitionInstructions.match_continues:
{
  "id": "match_continues",
  "transitionName": "Match Continues",
  "description": "Resolve round with RPS logic and optional wrestling-style reversal, update scores, continue to next round",
  "priority": 10,
  "mechanicsGuidance": {
    "rules": [
      "Rock beats scissors",
      "Scissors beats paper",
      "Paper beats rock",
      "If both weapons map to same RPS value, round is a tie (no score change)",
      "Occasionally (unpredictably, NOT every round) incorporate wrestling-style dramatic reversal: battle initially favors one direction, shocking turnaround occurs mid-narrative, underdog delivers surprising counter, actual victor revealed through dramatic reversal",
      "Reversal must maintain absurdist logic and feel earned within narrative premise",
      "Generate 2-4 sentence narrative explaining round outcome",
      "Use boisterous announcer style with maximum entertainment value",
      "Include groan-inducing puns when appropriate",
      "Reference both weapons by name",
      "Reinforce that creative weapon choice mattered"
    ],
    "computation": "Compare selected weapons using RPS mappings, decide whether to include dramatic reversal based on random flag, generate boisterous narrative that makes outcome feel earned with optional reversal building tension, determine winner and update scores"
  },
  "rngConfig": {
    "operations": [
      "reversal_decision"
    ],
    "guidance": "Randomly decide whether this round includes a wrestling-style dramatic reversal. Use low probability (20-30% chance) to maintain surprise and avoid predictable pattern. Output boolean flag for template resolution."
  },
  "stateDelta": [
    {
      "op": "rng",
      "path": "game.includeReversal",
      "choices": [
        true,
        false
      ],
      "probabilities": [
        0.25,
        0.75
      ]
    },
    {
      "op": "increment",
      "path": "players.{{winnerId}}.roundsWon",
      "value": "{{roundWinIncrement}}"
    },
    {
      "op": "set",
      "path": "players.player1.selectedWeapon",
      "value": null
    },
    {
      "op": "set",
      "path": "players.player2.selectedWeapon",
      "value": null
    },
    {
      "op": "increment",
      "path": "game.currentRound",
      "value": 1
    },
    {
      "op": "set",
      "path": "game.lastRoundNarrative",
      "value": "{{roundNarrative}}"
    },
    {
      "op": "delete",
      "path": "game.includeReversal"
    },
    {
      "op": "set",
      "path": "players.player1.actionRequired",
      "value": true
    },
    {
      "op": "set",
      "path": "players.player2.actionRequired",
      "value": true
    },
    {
      "op": "set",
      "path": "game.currentPhase",
      "value": "round_selection"
    }
  ],
  "messages": {
    "private": null,
    "public": {
      "template": "{{roundNarrative}}"
    }
  },
  "requiredStateFields": [
    "game.currentPhase",
    "game.currentRound",
    "players.player1.selectedWeapon",
    "players.player2.selectedWeapon",
    "players.player1.weapons",
    "players.player2.weapons",
    "players.player1.weaponRPSMapping",
    "players.player2.weaponRPSMapping",
    "players.player1.roundsWon",
    "players.player2.roundsWon"
  ]
}

transitionInstructions.match_won:
{
  "id": "match_won",
  "transitionName": "Match Won",
  "description": "Resolve final round, announce winner with celebratory narrative and ridiculous prize, end match",
  "priority": 5,
  "mechanicsGuidance": {
    "rules": [
      "Rock beats scissors",
      "Scissors beats paper",
      "Paper beats rock",
      "Same RPS values tie",
      "Occasionally (unpredictably) final round includes wrestling-style dramatic reversal",
      "Match winner is first player to reach 2 roundsWon",
      "Ridiculous prize must be completely nonsensical with no real value",
      "Deliver prize announcement with boisterous announcer energy",
      "Generate complete match-end message: final round outcome narrative (if needed) with optional dramatic reversal, over-the-top celebratory commentary celebrating winner and referencing winning weapons, ridiculous prize announcement"
    ],
    "computation": "If final round not yet resolved: compare weapons using RPS logic, optionally include dramatic reversal, generate boisterous final round narrative. Identify match winner. Create celebratory match-end commentary celebrating winner, referencing winning weapons, with maximum entertainment value. Announce hilariously absurd prize (rubber chickens, cardboard trophies, fake memberships, honorary titles with no meaning)."
  },
  "rngConfig": {
    "operations": [
      "reversal_decision",
      "prize_generation"
    ],
    "guidance": "Randomly decide whether final round includes wrestling-style dramatic reversal (same probability as other rounds, ~20-30% chance). Prize generation may involve randomness in selecting type of absurd award from various categories."
  },
  "stateDelta": [
    {
      "op": "rng",
      "path": "game.includeReversal",
      "choices": [
        true,
        false
      ],
      "probabilities": [
        0.25,
        0.75
      ]
    },
    {
      "op": "increment",
      "path": "players.{{winnerId}}.roundsWon",
      "value": "{{roundWinIncrement}}"
    },
    {
      "op": "set",
      "path": "game.gameEnded",
      "value": true
    },
    {
      "op": "set",
      "path": "players.player1.actionRequired",
      "value": false
    },
    {
      "op": "set",
      "path": "players.player2.actionRequired",
      "value": false
    },
    {
      "op": "set",
      "path": "players.player1.selectedWeapon",
      "value": null
    },
    {
      "op": "set",
      "path": "players.player2.selectedWeapon",
      "value": null
    },
    {
      "op": "delete",
      "path": "game.includeReversal"
    },
    {
      "op": "set",
      "path": "game.currentPhase",
      "value": "finished"
    }
  ],
  "messages": {
    "private": null,
    "public": {
      "template": "{{matchEndMessage}}"
